import React, { useEffect, useState } from "react";
import { useMutation, useQuery } from "@apollo/client";

import { Form, Input, Button, Menu, Dropdown } from "antd";
import { DownOutlined } from "@ant-design/icons";

import { v4 as uuidv4 } from "uuid";

import { GET_PEOPLE, GET_BOAT, ADD_BOAT } from "../../queries";

const AddBoat = () => {
  const [addBoat] = useMutation(ADD_BOAT);
  const { loading, error, data } = useQuery(GET_PEOPLE);
  console.log(data);
  //   if (loading) return "Loading...";
  //   if (error) return `Errror! ${error.message}`;

  const [form] = Form.useForm();
  const [, forceUpdate] = useState();

  // To disable submit button at the beginning.
  useEffect(() => {
    forceUpdate({});
  }, []);

  const onFinish = (values) => {
    const { year, make, model, price } = values;
    const id = uuidv4();

    addBoat({
      variables: {
        id,
        year,
        make,
        model,
        price,
      },
      //   optimisticResponse: {
      //     __typename: "Mutation",
      //     addPerson: {
      //       __typename: "Person",
      //       id,
      //       firstName,
      //       lastName,
      //     },
      //   },
      update: (proxy, { data: { addBoat } }) => {
        const data = proxy.readQuery({ query: GET_BOAT });
        proxy.writeQuery({
          query: GET_BOAT,
          data: {
            ...data,
            boats: [...data.boats, addBoat],
          },
        });
      },
    });
  };

  const menu = (
    <Menu>
      <Menu.Item>1st menu item</Menu.Item>
      <Menu.Item icon={<DownOutlined />} disabled>
        2nd menu item
      </Menu.Item>
      <Menu.Item disabled>3rd menu item</Menu.Item>
      <Menu.Item danger>a danger item</Menu.Item>
    </Menu>
  );

  return (
    <Form
      form={form}
      name="add-boat-form"
      layout="inline"
      onFinish={onFinish}
      size="large"
      style={{ marginBottom: "40px" }}
    >
      <Form.Item
        name="year"
        rules={[{ required: true, message: "Please input your boat year!" }]}
      >
        <Input placeholder="Year" />
      </Form.Item>
      <Form.Item
        name="make"
        rules={[{ required: true, message: "Please input boat make!" }]}
      >
        <Input placeholder="Make" />
      </Form.Item>
      <Form.Item
        name="model"
        rules={[{ required: true, message: "Please input boat model!" }]}
      >
        <Input placeholder="Model" />
      </Form.Item>
      <Form.Item
        name="price"
        rules={[{ required: true, message: "Please input boat price" }]}
      >
        <Input placeholder="Price" />
      </Form.Item>
      <Dropdown overlay={menu}>
        <a className="ant-dropdown-link" onClick={(e) => e.preventDefault()}>
          Hover me <DownOutlined />
        </a>
      </Dropdown>
      <Form.Item shouldUpdate={true}>
        {() => (
          <Button
            type="primary"
            htmlType="submit"
            disabled={
              !form.isFieldsTouched(true) ||
              form.getFieldsError().filter(({ errors }) => errors.length).length
            }
          >
            Add Boat
          </Button>
        )}
      </Form.Item>
    </Form>
  );
};

export default AddBoat;
